import React from 'react';
class Product extends React.Component {

    // Constructor 
    constructor(props) {
        super(props);

        this.state = {
            items: [],
            DataisLoaded: false
        };
    }

    // ComponentDidMount is used to
    // execute the code 
    componentDidMount() {
        fetch("https://fakestoreapi.com/products")
            .then((res) => res.json())
            .then((json) => {
                this.setState({
                    items: json,
                    DataisLoaded: true
                });
            })
    }
    render() {
        const { DataisLoaded, items } = this.state;
        if (!DataisLoaded) return <div>
            <h3 className='container mt-4 mb-4' style={{color:"red"}}> Pleses wait some time.... </h3> </div> ;

        return (
        <div className = "container mt-2">
            <h3> # Fetch data from an api in react </h3> <hr />  
                {/* code  */}
                <div className="height d-flex justify-content align-items-center">
                    <div className="card p-3">
                        <div className="d-flex justify-content-between align-items-center ">
                            <div className="mt-2">
                                <h4 className="text-uppercase">Ikea</h4>
                                <div className="mt-5">
                                    <h5 className="text-uppercase mb-0">Blanda Matt</h5>
                                    <h1 className="main-heading mt-0">VASE</h1>
                                    <div className="d-flex flex-row user-ratings">
                                        <div className="ratings"> <i className="fa fa-star"></i> <i className="fa fa-star"></i> <i className="fa fa-star"></i> <i className="fa fa-star"></i> </div>
                                        <h6 className="text-muted ml-1">4/5</h6>
                                    </div>
                                </div>
                            </div>
                            <div className="image"> <img src="https://i.imgur.com/MGorDUi.png" width="200"/> </div>
                        </div>
                        <div className="d-flex justify-content-between align-items-center mt-2 mb-2"> <span>Available colors</span>
                            <div className="colors"> <span></span> <span></span> <span></span> <span></span> </div>
                        </div>
                        <p>A great option weather you are at office or at home. </p> <button class="btn btn-danger">Add to cart</button>
                    </div>
                </div>
                {/* end code */}
                <table className="table table-bordered">
                    <thead>
                        <tr>
                        <th scope="col">Sr No.</th>
                        <th>title</th>
                        <th>description</th>
                        <th>price</th>
                        <th>category</th>
                        <th>image</th>
                        <th>rating.rate</th>
                        <th>rating.count</th>
                        </tr>
                    </thead>
                    <tbody id="demo">{
                    items.map((item) => ( 
                    <div className="height d-flex justify-content align-items-center">
                        <div className="card p-3">
                            <div className="d-flex justify-content-between align-items-center ">
                                <div className="mt-2">
                                    <h4 className="text-uppercase">Ikea</h4>
                                    <div className="mt-5">
                                        <h5 className="text-uppercase mb-0">Blanda Matt</h5>
                                        <h1 className="main-heading mt-0">VASE</h1>
                                        <div className="d-flex flex-row user-ratings">
                                            <div className="ratings"> <i className="fa fa-star"></i> <i className="fa fa-star"></i> <i className="fa fa-star"></i> <i className="fa fa-star"></i> </div>
                                            <h6 className="text-muted ml-1">4/5</h6>
                                        </div>
                                    </div>
                                </div>
                                <div className="image"> <img src="https://i.imgur.com/MGorDUi.png" width="200"/> </div>
                            </div>
                            <div className="d-flex justify-content-between align-items-center mt-2 mb-2"> <span>Available colors</span>
                               <div className="colors"> <span></span> <span></span> <span></span> <span></span> </div>
                            </div>
                            <p>A great option weather you are at office or at home. </p> <button class="btn btn-danger">Add to cart</button>
                        </div>
                      </div>
                     ))
                    }
                    </tbody>
                </table> 
        </div>
    );
}
}

export default Product
